document.addEventListener("DOMContentLoaded",(function(){const pageFlip=new St.PageFlip(document.getElementById("bauhausBook"),{width:360,height:360,size:"stretch",minWidth:315,minHeight:315,maxWidth:1200,maxHeight:1200,maxShadowOpacity:.5,showCover:true,mobileScrollSupport:false});pageFlip.loadFromHTML(document.querySelectorAll(".page"));document.querySelector(".page-total").innerText=pageFlip.getPageCount();document.querySelector(".page-orientation").innerText=pageFlip.getOrientation();document.querySelector(".btn-prev").addEventListener("click",(()=>{pageFlip.flipPrev()}));document.querySelector(".btn-next").addEventListener("click",(()=>{pageFlip.flipNext()}));pageFlip.on("flip",(e=>{document.querySelector(".page-current").innerText=e.data+1}));pageFlip.on("changeState",(e=>{document.querySelector(".page-state").innerText=e.data}))}));